<template>
    <div class="uk-slidenav-position uk-width-large-3-4 uk-container-center" data-uk-slideshow="">
        <h3 class="uk-margin-remove">{{ gallery.title }}</h3>
        <img class="uk-display-block" src="data:image/jpeg;base64," alt="Slideshow">
        <p v-if="gallery.description" class="uk-margin-remove">{{ gallery.description }}</p>
        <a v-if="showLink" href="#">{{ 'more' | trans }} <i class="uk-icon uk-icon-arrow-right"></i></a>
    </div>
</template>
<script>
    module.exports = {
        props: ['id', 'limit', 'showLink'],

        data: function() {
            return {
                gallery: ''
            }
        },

        ready: function () {
            this.$resource('api/gallery{/id}', {id: this.id}).get()
                    .then(function(res){
                this.$set('gallery', res.data)
            });
        }
    };
</script>